import osos.environ['PROJ_LIB'] = r'c:\SDK\Anaconda3\Library\share'import numpy as npimport matplotlib.pyplot as pltfrom mpl_toolkits.basemap import Basemapfrom itertools import chaindef draw_map(m, scale=0.2):    # draw a shaded-relief image    m.shadedrelief(scale=scale)        # lats and longs are returned as a dictionary    lats = m.drawparallels(np.linspace(-90, 90, 13))    lons = m.drawmeridians(np.linspace(-180, 180, 13))    # keys contain the plt.Line2D instances    lat_lines = chain(*(tup[1][0] for tup in lats.items()))    lon_lines = chain(*(tup[1][0] for tup in lons.items()))    all_lines = chain(lat_lines, lon_lines)        # cycle through these lines and set the desired style    for line in all_lines:        line.set(linestyle='-', alpha=0.3, color='w')#{u'lat': -5.4715453, u'lng': 122.585339}}],#fig = plt.figure(figsize=(5, 5))#m = Basemap(projection='ortho', resolution=None,            #width=8E6, height=8E6,             #lat_0=45, lon_0=-100,)#            lat_0=-5.4715453, lon_0=122.585339,)#m.etopo(scale=1, alpha=0.7)#draw_map(m)# Map (long, lat) to (x, y) for plotting#x, y = m(-122.5, -5.4)#plt.plot(x, y, 'ok', markersize=5)#plt.text(x, y, ' Seattle', fontsize=12)#plt.show()#map = Basemap(llcrnrlon=3.75,llcrnrlat=39.75,urcrnrlon=4.35,urcrnrlat=40.15, epsg=5520)#map = Basemap(llcrnrlon=122.58,llcrnrlat=-5.47,urcrnrlon=122.58,urcrnrlat=-5.47, epsg=5520)map = Basemap(lat_0=-5.47, lon_0=122.58, width=8E6, height=8E6, epsg=5520)map.arcgisimage(service='ESRI_Imagery_World_2D', xpixels = 1500, verbose= True)plt.show()